mapper.select:
select

from dd_private_data
where DD_DATA_TASK_ID = #{id} AND DD_DATA_NAME = #{Name}

mapper.update:
begin

    update dd_private_data

        DD_DATA_PUBLISH_STATE = 1

    where DD_DATA_ID = ${item.ddDataId} OR DD_DATA_PARENT_ID = ${item.ddDataId};

end;

begin

    update dd_private_data

        DD_DATA_PUBLISH_STATE = 0

    where DD_DATA_ID = ${item.ddDataId} OR DD_DATA_PARENT_ID = ${item.ddDataId};

end;
